package steps;import helpers.DynamicDriverManager;import io.cucumber.java.en.And;import io.cucumber.java.en.When;import pageobjects.HomePage;import static org.testng.Assert.assertTrue;public class HomePageSteps {    public DynamicDriverManager driverManger;    public HomePageSteps(DynamicDriverManager driverManager) {        this.driverManger = driverManager;    }    @When("^I navigate to home page$")    public void navigateToHomePage() {        HomePage homePage = new HomePage(driverManger, true);        homePage.waitForPage(homePage.homePageDiv);        homePage.acceptCookiesButton.click();        assertTrue(homePage.logoLocale.getText().equals(".co.uk"));    }    @When("^I navigate to Book section$")    public void navigateToSection() {        HomePage homePage = new HomePage(driverManger, true);        homePage.booksSection.click();        assertTrue(homePage.pageTitle.getText().equals("Books"), "Books section is not loaded.");    }    @And("^I search for (.*)$")    public void searchForBook(String bookTitle) {        HomePage homePage = new HomePage(driverManger);        homePage.typeWithClear(homePage.searchTextBox, bookTitle);        homePage.searchSubmitButton.click();    }}